mapper.select:
SELECT t.id, t.org_name, t.parent_id, t.org_type, t.status, t.create_user, t.create_time, t.update_user, t.update_time, t.old_id, t.product_line, t.dept_id, t.sort_number, t.address, t.longitude, t.latitude, t.org, t.logo, (select wm_concat(toe.extend_info) from tab_organization_extend toe where toe.extend_type = '1' and toe.org_id = t.id) as domain, t.mchid, t.mid, t.tid, t.email, t1.org_name as parent_org_name from tab_organization_info t left join tab_organization_info t1 on t1.id = t.parent_id WHERE 1=1 AND t.status = 1
AND t.id = #{id}
AND t.parent_id = #{parent_id}
and t.org_name = #{org_name,jdbcType=NUMERIC}'
AND t.product_line = #{product_line}
AND t.org_kind = #{orgKind}
ORDER BY t.sort_number ASC, t.id ASC

SELECT t.*,t.id as buId FROM tab_organization_info t WHERE 1=1 AND status = 1
and id in (${ids})
and org_type = #{org_type}
and org_name like '%${org_name}%'
and org_name = #{strict_org_name, jdbcType=VARCHAR}
ORDER BY sort_number ASC, id ASC

SELECT t.*, t.org_kind as orgKind FROM
tab_organization_info t
WHERE id = #{id}

update.foreach:
            UPDATE tab_organization_info 

                status = #{item.status, jdbcType=NUMERIC},
                update_user = #{item.update_user, jdbcType=VARCHAR}, 
		  		update_time = #{item.update_time, jdbcType=VARCHAR}

               WHERE id = #{item.id}

mapper.select:
select tdca.*, tdd.id as pos_id, tdd.device_name as device_name, tdd.simple_name as simple_name, tdca.id as company_card_id from tab_data_company_account tdca left join tab_data_device tdd on tdd.account_id = tdca.ID where tdca.STATUS = 1 and tdd.STATUS = 1
and tdd.city_id = #{city_id,jdbcType=NUMERIC}
ORDER BY tdca.id ASC
