mapper.select:
SELECT t.id, t.org_name, t.parent_id, t.org_type, t.status, t.create_user, t.create_time, t.update_user, t.update_time, t.product_line, t.sort_num, t.address, t.logo, t.email, t1.org_name as parent_org_name from tab_organization_info t left join tab_organization_info t1 on t1.id = t.parent_id WHERE 1=1 AND t.status = 1
AND t.id = #{id}
AND t.parent_id = #{parent_id}
and t.org_name = #{org_name,jdbcType=NUMERIC}'
AND t.product_line = #{product_line}
AND t.org_kind = #{orgKind}
ORDER BY t.sort_num ASC, t.id ASC

SELECT tof.id , tof.org_name, tof.parent_id, tof.org_type , tof.status, (case when tof.org_type = '2' then tof.id end) city_id, (case when tof.org_type= '2' then '地区' when tof.org_type= '3' then '团队' when tof.org_type= '4' then '校区' else '' end) org_type_name, tof.address, tof.product_line, tof1.org_name as parent_org_name, tof.logo, tof.phone, tof.org_kind as orgKind, tof.email, tof.short_org_name as shortOrgName FROM tab_organization_info tof left join tab_organization_info tof1 on tof1.id = tof.parent_id WHERE 1=1
and tof.org_type = #{org_type}
and tof.org_name like concat('%', #{org_name}, '%')
and tof.org_name = #{strict_org_name, jdbcType=VARCHAR}
ORDER BY tof.sort_num

SELECT t.*, t.org_kind as orgKind FROM
tab_organization_info t
WHERE id = #{id}

select max(sort_num) + 1
from tab_organization_info toi
where toi.parent_id = #{parentId}
