SELECT TOID.*, TTI.TEACHER_NAME,
TDD.NAME AS SUBJECT_NAME, TC.SUBJECT_ID as SUBJECT_ID FROM TAB_ORDER_INFO_DETAIL TOID LEFT JOIN TAB_TEACHER_INFO TTI ON TTI.ID = TOID.TEACHER_ID LEFT JOIN T_COURSE TC ON TC.SUBJECT_ID = TOID.COURSE_ID LEFT JOIN
TP_SUBJECT TDD ON TDD.ID = TC.SUBJECT_ID WHERE TOID.STUDENT_ID = #{student_id,jdbcType=NUMERIC}

SELECT CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.ID ELSE TOID.ID END AS ID , TOIII.ID AS ORDER_ID , TOIII.ENCODING , TOIII.STUDENT_ID , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.COURSE_ID ELSE TOID.COURSE_ID END AS COURSE_ID , TOIII.BUSINESS_TYPE , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.FORMER_UNIT_PRICE ELSE TOID.FORMER_UNIT_PRICE END AS FORMER_UNIT_PRICE , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.FORMER_SUM_PRICE ELSE TOID.FORMER_SUM_PRICE END AS FORMER_SUM_PRICE , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.DISCOUNT_UNIT_PRICE ELSE TOID.DISCOUNT_UNIT_PRICE END AS DISCOUNT_UNIT_PRICE , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.DISCOUNT_SUM_PRICE ELSE TOID.DISCOUNT_SUM_PRICE END AS DISCOUNT_SUM_PRICE , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.MANAGE_FEE ELSE TOID.PRE_FORWARD END AS PRE_FORWARD , COURSE_INFO.START_DATE , COURSE_INFO.END_DATE , COURSE_INFO.START_TIME , COURSE_INFO.END_TIME , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.COURSE_TOTAL_COUNT ELSE TOID.COURSE_TOTAL_COUNT END AS COURSE_TOTAL_COUNT , COURSE_INFO.TEACHER_ID , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.COURSE_SURPLUS_COUNT ELSE TOID.COURSE_SURPLUS_COUNT END AS COURSE_SURPLUS_COUNT , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.SURPLUS_COST ELSE TOID.SURPLUS_COST END AS SURPLUS_COST , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.QUIT_FLAG ELSE TOID.QUIT_FLAG END AS QUIT_FLAG , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.CREATE_USER ELSE TOID.CREATE_USER END AS CREATE_USER , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.CREATE_TIME ELSE TOID.CREATE_TIME END AS CREATE_TIME , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.UPDATE_USER ELSE TOID.UPDATE_USER END AS UPDATE_USER , CASE WHEN TOIII.PAY_STATUS = 1 THEN TOC.UPDATE_TIME ELSE TOID.UPDATE_TIME END AS UPDATE_TIME , TOID.EXTEND_COLUMN , TOID.EXTEND_COLUMN2 , TOID.EXTEND_COLUMN3 , TOID.EXTEND_COLUMN4 , TOID.V3_MARK , TOID.IMMEDIATE_REDUCE , COURSE_INFO.COURSE_NAME , COURSE_INFO.COURSE_SEASON_NAME , COURSE_INFO.SEASON , COURSE_INFO.SEASON_ID AS SEASON_ID , COURSE_INFO.GRADE_NAME , COURSE_INFO.GRADE_ID AS GRADE_ID , COURSE_INFO.BRANCH_NAME , COURSE_INFO.BRANCH_ID, COURSE_INFO.BRANCH_ID COURSE_BRANCH_ID, COURSE_INFO.SUBJECT_NAME , COURSE_INFO.SUBJECT_ID AS SUBJECT_ID , TTI.TEACHER_NAME , TTI.PHOTO AS TEACHER_HEAD , COURSE_INFO.COURSE_NO , COURSE_INFO.COURSE_COUNT , TOC.COURSE_SURPLUS_COUNT AS COURSE_SURPLUS_COUNT_ING , TOIII.CHECK_STATUS, TCOC2.ORDER_COURSE_ID as SRC_ORDER_COURSE_ID, TOID.rule_id as RULE_ID, TPR.RULE_NAME as RULE_NAME, TOID.COURSE_PACK_TYPE, TOID.COURSE_PACK_PRICE, tu.UNION_NAME as COURSE_PACK_NAME, TOID.IS_SEND, TOC.STATUS, course_info.is_approve, tol.status lock_status, w.combo_name FROM TAB_ORDER_INFO TOIII LEFT JOIN( SELECT ID , ORDER_ID , COURSE_ID FROM T_ORDER_COURSE WHERE ORDER_ID = #{order_id,jdbcType=NUMERIC} UNION SELECT ID , ORDER_ID , COURSE_ID FROM TAB_ORDER_INFO_DETAIL WHERE ORDER_ID = #{order_id,jdbcType=NUMERIC} ) DETAIL_ROW ON DETAIL_ROW.ORDER_ID = TOIII.ID LEFT JOIN TAB_ORDER_INFO_DETAIL TOID ON DETAIL_ROW.ORDER_ID = TOID.ORDER_ID AND DETAIL_ROW.ID = TOID.ID LEFT JOIN T_ORDER_COURSE TOC ON TOC.ID = DETAIL_ROW.ID AND DETAIL_ROW.ORDER_ID = TOC.ORDER_ID LEFT JOIN( SELECT TC.ID , TC.COURSE_NAME , TC.SEASON_ID , TTS.COURSE_SEASON_NAME , TTS.SEASON AS SEASON , TC.GRADE_ID , TDG.GRADE_NAME , TOOI.ID AS BRANCH_ID , TOOI.ORG_NAME AS BRANCH_NAME , TDD.NAME AS SUBJECT_NAME , TC.ATTEND_CLASS_PERIOD AS ATTEND_CLASS_PERIOD , TC.COURSE_NO , TC.COURSE_COUNT , TC.SUBJECT_ID , TC.START_DATE , TC.END_DATE , TC.START_TIME , TC.END_TIME , TC.TEACHER_ID, tc.is_approve FROM T_COURSE TC LEFT JOIN TAB_TIME_SEASON TTS ON TTS.ID = TC.SEASON_ID LEFT JOIN TAB_DATA_GRADE TDG ON TDG.ID = TC.GRADE_ID LEFT JOIN TAB_ORGANIZATION_INFO TOOI ON TOOI.ID = TC.BRANCH_ID AND TOOI.ORG_TYPE = 4 LEFT JOIN TP_SUBJECT TDD ON TDD.ID = TC.SUBJECT_ID ) COURSE_INFO ON COURSE_INFO.ID = DETAIL_ROW.COURSE_ID LEFT JOIN TAB_TEACHER_INFO TTI ON TTI.ID = COURSE_INFO.TEACHER_ID LEFT JOIN TC_ORDER_COURSE TCOC ON TCOC.ORDER_ID = TOC.ORDER_ID AND TCOC.ORDER_COURSE_ID = TOC.ID AND TCOC.TRANSFER_FLAG = 1 LEFT JOIN TC_ORDER_COURSE TCOC2 ON TCOC.CHANGE_ID = TCOC2.CHANGE_ID AND TCOC.ORDER_ID = TCOC2.ORDER_ID AND TCOC2.TRANSFER_FLAG = 0 LEFT JOIN TAB_PRIVILEGE_RULE TPR ON TPR.ID = TOID.rule_id left join T_UNION tu on tu.id = TOID.COURSE_PACK_TYPE left join t_order_lock tol on tol.order_id = TOIII.ID left join t_course_combo_wfd w on toid.extend_column2 = w.id WHERE TOIII.ID = #{order_id,jdbcType=NUMERIC}
and (TOC.COURSE_ID = #{course_id,jdbcType=NUMERIC} or TOID.COURSE_ID = #{course_id,jdbcType=NUMERIC})
order by TOID.COURSE_PACK_PRICE

select oc.id, o.id as order_id, o.order_no as encoding, o.student_id, o.business_type, ts.course_season_name, ps.name as subject_name, ti.teacher_name, c.start_time , c.end_time, oc.course_total_count, nvl(trans.in_times, 0) transfer_in_count, nvl(trans.out_times, 0) transfer_out_count, nvl(rm.times, 0) quit_lesson, att.att_times as attend_count, oc.course_surplus_count as course_surplus_count_ing, c.id as course_id from t_order o left join t_order_course oc on o.id = oc.order_id left join t_course c on oc.course_id = c.id left join tp_subject ps on c.subject_id = ps.id left join tab_time_season ts on c.season_id = ts.id left join tab_teacher_info ti on c.teacher_id = ti.id left join (select b.order_course_id, sum(decode(b.transfer_flag, 0, 1, 0) * b.course_times) out_times, sum(decode(b.transfer_flag, 1, 1, 0) * b.course_times) in_times from t_order_change a join tc_order_course b on a.id = b.change_id where a.change_type = 2 and a.change_status = 5 group by b.order_course_id) trans on oc.id = trans.order_course_id left join (select b.order_course_id, sum(b.course_times) times from t_order_change a, tc_order_course b where a.id = b.change_id and a.change_status = 5 and a.change_type = 1 group by b.order_course_id) rm on oc.id = rm.order_course_id left join (select order_course_id, sum(decode(a.attend_type, 10, 0, 20, 0, 30, 0, -1, 0, 1)) att_times --考勤状态需重新考虑 from t_attendance a group by a.order_course_id) att on att.order_course_id = oc.id left join tab_organization_info toooi on toooi.id = o.bu_id where o.business_type = 1 and o.STUDENT_ID = #{student_id,jdbcType=NUMERIC}
and toooi.product_line = #{product_line,jdbcType=NUMERIC}
and o.branch_id = #{branch_id,jdbcType=NUMERIC}
and o.branch_id in
#{item}
and o.order_status = 1
AND c.SEASON_ID = #{season_id,jdbcType=NUMERIC}
AND c.SUBJECT_ID = #{subject_id,jdbcType=NUMERIC}

SELECT TDD.NAME as subject_name, TC.SUBJECT_ID, TOID.*, TTI.TEACHER_NAME, ATTEND_INFO.ATTEND_COUNT, toc.course_surplus_count as course_surplus_count_ing, toi.check_status, toi.pay_status, TC.id as course_id FROM TAB_ORDER_INFO_DETAIL TOID LEFT JOIN T_COURSE TC ON TC.ID = TOID.COURSE_ID
LEFT JOIN TP_SUBJECT TDD ON TDD.ID = TC.SUBJECT_ID
LEFT JOIN TAB_TEACHER_INFO TTI ON TTI.ID = TOID.TEACHER_ID LEFT JOIN (select t.order_course_id as order_detail_id, count(t.id) as attend_count from t_attendance t where t.attend_type in (2, 3, 4, 5, 6, 7, 8, 9) group by t.order_course_id) ATTEND_INFO ON ATTEND_INFO.ORDER_DETAIL_ID = TOID.ID left join t_order_course toc on toc.id = toid.id left join tab_order_info toi on toi.id = toid.order_id left join tab_organization_info toooi on toooi.id = toi.bu_id WHERE TOID.STUDENT_ID = #{student_id,jdbcType=NUMERIC}
and toooi.product_line = #{product_line,jdbcType=NUMERIC}
AND TOID.BUSINESS_TYPE = 2
AND TC.SUBJECT_ID = #{subject_id,jdbcType=NUMERIC}

SELECT TOID.*, ATTD.attend_count, toc.course_surplus_count as course_surplus_count_ing, toi.check_status, toi.pay_status, toi.valid_status, toc.course_id, tc.course_name, w.combo_name, w.id combo_id FROM TAB_ORDER_INFO_DETAIL TOID LEFT JOIN (select t.order_course_id as order_detail_id, count(t.id) as attend_count from t_attendance t where t.attend_type in (31) and t.TS_FLAG = 'S' group by t.order_course_id) ATTD ON ATTD.ORDER_DETAIL_ID = TOID.ID left join t_order_course toc on toc.id = toid.id left join t_order_course_combo occ on toc.id = occ.order_course_id left join t_course_combo_wfd w on toid.EXTEND_COLUMN2 = w.id join t_course tc on tc.id = toid.course_id left join tab_order_info toi on toi.id = toid.order_id left join tab_organization_info toooi on toooi.id = toi.bu_id WHERE TOID.STUDENT_ID = #{student_id, jdbcType = NUMERIC} AND TOID.BUSINESS_TYPE = 3 and toooi.product_line = #{product_line,jdbcType=NUMERIC} AND TOID.create_time BETWEEN #{beginDate, jdbcType = DATE} AND #{endDate, jdbcType = DATE}
and toi.branch_id in
#{item}

SELECT TOID.*, COURSE_INFO.COURSE_NAME, COURSE_INFO.COURSE_SEASON_NAME, COURSE_INFO.SEASON, COURSE_INFO.GRADE_NAME, COURSE_INFO.BRANCH_NAME, COURSE_INFO.SUBJECT_NAME, w.combo_name FROM TAB_ORDER_INFO_DETAIL TOID LEFT JOIN (SELECT TC.ID, TC.COURSE_NAME, TC.SEASON_ID, TTS.COURSE_SEASON_NAME, TTS.SEASON AS SEASON, TC.GRADE_ID, TDG.GRADE_NAME, TOOI.ID AS BRANCH_ID, TOOI.ORG_NAME AS BRANCH_NAME,
TDD.NAME AS SUBJECT_NAME, TC.ATTEND_CLASS_PERIOD AS ATTEND_CLASS_PERIOD FROM T_COURSE TC LEFT JOIN TAB_TIME_SEASON TTS ON TTS.ID = TC.SEASON_ID LEFT JOIN TAB_DATA_GRADE TDG ON TDG.ID = TC.GRADE_ID LEFT JOIN TAB_ORGANIZATION_INFO TOOI ON TOOI.ID = TC.BRANCH_ID AND TOOI.ORG_TYPE = 4
LEFT JOIN TP_SUBJECT TDD ON TDD.ID = TC.SUBJECT_ID
) COURSE_INFO ON COURSE_INFO.ID = TOID.COURSE_ID left join t_course_combo_wfd w on toid.extend_column2 = w.id WHERE TOID.STUDENT_ID = #{student_id,jdbcType=NUMERIC}
