mapper.select:
select bu_id, bu_name, course_branch_id, course_branch_name, teacher_id, teacher_code, teacher_name, assteacher_id, assteacher_code, assteacher_name, course_id, course_no, course_name, grade_id, grade_name, subject_id, subject_name, teacher_group_name, start_time, end_time, hour_length, schedule_date, course_time, sum(nvl(attend_students_num,0)) attend_students_num, sum(nvl(normal_attend_students_num,0)) normal_attend_students_num, sum(nvl(hangup_students_num,0)) hangup_students_num, max(nvl(tobe_students_num,0)) tobe_students_num, max(nvl(listening_students_num,0)) listening_students_num, max(nvl(listen_rejection_students_num,0)) listen_rejection_students_num from t_teacher_workload a where a.bu_id = #{bu_id}
and a.course_branch_id = #{branch_id}
and a.course_branch_id in (${validBranchIds})
and a.schedule_date >= #{start_date, jdbcType=VARCHAR}
and a.business_date >= #{start_date, jdbcType=VARCHAR}
and a.schedule_date <= #{end_date, jdbcType=VARCHAR}
and a.business_date <= #{end_date, jdbcType=VARCHAR}
and a.course_name like '%${course_name}%'
and a.assteacher_id is not null
group by a.bu_id, a.bu_name, a.course_branch_id, a.course_branch_name, a.teacher_id, a.teacher_code, a.teacher_name, a.assteacher_id, a.assteacher_code, a.assteacher_name, a.course_id, a.course_no, a.course_name, a.grade_id, a.grade_name, a.subject_id, a.subject_name, a.teacher_group_name, a.start_time, a.end_time, a.hour_length, a.schedule_date, a.course_time order by a.course_no, a.course_time

select encoding,student_name from ( select sum(case when t1.attend_type = 10 then -1 else 1 end ) attend_students_num, sum(case when t1.attend_type = 10 and t1.last_attend_type = 12 then -1 when t1.attend_type = 12 then 1 else 0 end ) normal_attend_students_num, sum(case when t1.attend_type = 10 and t1.last_attend_type = 11 then -1 when t1.attend_type = 11 then 1 else 0 end ) hangup_students_num, t1.teacher_id, t1.course_times, t1.course_id, t1.attend_branch_id, t1.scheduling_id, t1.start_time, t1.end_time, t1.student_id, tsi.encoding, tsi.student_name from (select sdl.course_date, att.attend_date, att.student_id, nvl(att.teacher_id,sdl.teacher_id) as teacher_id, sdl.course_times, sdl.course_id, att.attend_branch_id attend_branch_id, att.scheduling_id, att.attend_type, att.last_attend_type, sdl.start_time, sdl.end_time from (select * from t_attendance_ht where ts_flag = 'S' ) att join t_order_course ordercourse on att.order_course_id = ordercourse.id join t_order torder on ordercourse.order_id = torder.id and torder.business_type = 1 and torder.order_status = 1 left join t_course_scheduling sdl on att.scheduling_id = sdl.id left join t_order_course_times ct on ordercourse.id = ct.ocid and sdl.course_times = ct.times and ct.is_valid = 1 where sdl.course_id = #{course_id, jdbcType=NUMERIC}
and to_char(to_date(sdl.course_date,'yyyyMMdd'),'yyyy-MM-dd') >= #{start_date, jdbcType=VARCHAR}
and to_char(att.attend_date, 'yyyy-mm-dd') >= #{start_date, jdbcType=VARCHAR}
and to_char(to_date(sdl.course_date,'yyyyMMdd'),'yyyy-MM-dd') <= #{end_date, jdbcType=VARCHAR}
and to_char(att.attend_date, 'yyyy-mm-dd') <= #{end_date, jdbcType=VARCHAR}
) t1,tab_student_info tsi where t1.student_id = tsi.id and t1.course_id = #{course_id, jdbcType=NUMERIC} and t1.course_times = #{course_times, jdbcType=NUMERIC} group by t1.student_id, t1.attend_branch_id, t1.teacher_id, t1.scheduling_id, t1.course_times, t1.course_id, t1.start_time, t1.end_time, student_id, tsi.encoding, tsi.student_name ) where normal_attend_students_num = 1 order by student_name
