mapper.select:
SELECT t1.task_id, t1.task_flow, t1.task_name, t1.run_time, t1.run_result, case when t1.run_result = 1 then '成功' else '失败' end AS run_result_name, t1.remark, t2.type, case when t2.type = 'common' then '通用' when t2.type = 'bjk' then '班级课' when t2.type = 'ydy' then '一对一' when t2.type = 'wfd' then '晚辅导' end AS type_name, t3.task_table_lastval, t3.task_table_currentval, t3.task_table_type, case when t3.task_table_type = 'INCREMENT' then '增量' when t3.task_table_type = 'FULL' then '全量' end AS task_table_type_name FROM t_report_run_result t1, t_report_task_settings t2, t_task_table_result t3 where t1.task_id = t2.task_id and t1.task_flow = t3.task_flow and t1.task_id = t3.task_id
and date_format(t1.run_time, '%Y-%m-%d') = #{run_time}
and t2.type = #{type}
and t1.task_flow like '${task_flow}%'
and t1.run_result = #{run_result}
and (#{task_table_val} between t3.task_table_lastval and t3.task_table_currentval)
and t1.task_id = #{task_id}
order by t1.run_time desc

mapper.update:
update t_report_run_result set
remark = #{remark,jdbcType=VARCHAR},
run_time = #{runTime,jdbcType=TIMESTAMP}, run_result = #{runResult,jdbcType=NUMERIC}, start_time = #{startTime,jdbcType=NUMERIC}, end_time = #{endTime,jdbcType=NUMERIC} where id = #{id}

mapper.select:
SELECT trrr.id, trrr.task_id as taskId, trrr.task_name as taskName, trrr.run_time as runTime, trrr.run_result as runResult, trrr.task_flow as taskFlow, trrr.remark, trrr.start_time as startTime, trrr.end_time as endTime FROM t_report_run_result trrr WHERE trrr.task_id = #{taskId,jdbcType=VARCHAR}
and trrr.task_flow = #{taskFlow,jdbcType=VARCHAR}
ORDER BY trrr.task_flow DESC
