mapper.select:
select id, ENCODING, EMPLOYEE_NAME,SEX,ID_CARD, EMAIL,PHONE,ADDRESS,DESCRIPTION,STATUS,CREATE_USER,CREATE_TIME,UPDATE_USER,UPDATE_TIME
  from TAB_EMPLOYEE_INFO
 where id = #{id}

mapper.update:
update TAB_EMPLOYEE_INFO set
UPDATE_TIME = sysdate()
,ENCODING = #{encoding,jdbcType = VARCHAR}
,EMPLOYEE_NAME = #{employeeName,jdbcType = VARCHAR}
,SEX = #{sex,jdbcType = NUMERIC}
,ID_CARD = #{identificationCard,jdbcType = VARCHAR}
,email = #{email,jdbcType = VARCHAR}
,phone = #{phone,jdbcType = VARCHAR}
,address = #{address,jdbcType = VARCHAR}
,DESCRIPTION = #{description,jdbcType = VARCHAR}
,STATUS = #{status,jdbcType = NUMERIC}
,UPDATE_USER = #{updateUser,jdbcType = NUMERIC}
,UPDATE_USER = #{updateTime,jdbcType = TIMESTAMP}
where id = #{id,jdbcType = NUMERIC}
