mapper.select:
SELECT TOI.ID AS ID_SUB, TOI.ORG_NAME AS TEXT_SUB, TOI2.ID parentId, TOI2.ID AS ID, TOI2.ORG_NAME AS TEXT, toi.org_type||'' as type, TOI2.ID buId, TOI2.Parent_Id cityId FROM ebs_accountorg_rel PUS LEFT JOIN TAB_ORGANIZATION_INFO TOI ON TOI.ID = PUS.Orgid LEFT JOIN TAB_ORGANIZATION_INFO TOI2 ON TOI2.ID = TOI.Parent_Id where 1=1
AND PUS.Accountid = #{userId}
order by TEXT asc

SELECT TOI.ID AS ID_SUB, TOI.ORG_NAME AS TEXT_SUB, TOI2.ID parentId, TOI2.ID AS ID, TOI2.ORG_NAME AS TEXT, toi2.org_type||'' as type, TOI2.ID buId, TOI2.Parent_Id cityId FROM PUB_USER_SCHOOL PUS LEFT JOIN TAB_ORGANIZATION_INFO TOI ON TOI.ID = PUS.SCH_ID LEFT JOIN TAB_ORGANIZATION_INFO TOI2 ON TOI2.ID = TOI.Parent_Id where 1=1
AND PUS.USER_ID = #{userId}
order by TEXT asc
